import styled from "styled-components";

export default function SideBar() {
  const Sidebar = styled.div`
    display: flex;
    flex-direction: column;
    background-color: #003483;
    color: white;
    text-align: center;
    height: 100%;
    width: 200px;
    position: fixed;
    left: 0;
    nav {
      padding: 10px 15px;
      h1 {
        margin: 0 0 15px 0;
      }
      button {
        background-color: #003483;
        border: none;
        text-decoration: none;
        cursor: pointer;
        i {
          color: white;
        }
      }
      ul {
        padding: 0;
        list-style: none;
        li {
          margin-block-start: 0em;
          margin-bottom: 10px;
          a {
            text-decoration: none;
            color: white;
          }
        }
      }
    }
    @media (max-width: 768px) {
      width: 100%;
      height: auto;
      top: 0;
    }
  `;

  return (
    <Sidebar>
      <nav>
        <h1>Tecnoland</h1>
