import React, { useState } from "react";
import styled from "styled-components";

const Sidebar = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #003483;
  color: white;
  text-align: center;
  height: 100%;
  width: 200px;
  position: fixed;
  left: 0;

  nav {
    padding: 10px 15px;
    h1 {
      margin: 0 0 15px 0;
    }
    button {
      background-color: #003483;
      border: none;
      text-decoration: none;
      cursor: pointer;
      display:flex;
      align-items:middle;
      i {
        color: white;
        font-size:20px;
      }
    }
    ul {
      padding: 0;
      list-style: none;
      li {
        margin-block-start: 0em;
        margin-bottom: 10px;
        a {
          text-decoration: none;
          color: white;
        }
      }
    }
  }

  @media (max-width: 768px) {
    width: 100%;
    height: auto;
    top: 0;
    button {
      display: block;
      position: absolute;
      top: 10px;
      left: 10px;
      z-index: 1;
    }
  }
`;

export default function SideBar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <Sidebar>
      <nav>
        <h1>Tecnoland</h1>
        <button onClick={toggleMenu}>
          <i className="fa-solid fa-bars"></i>
        </button>
        {isMenuOpen && (
          <div>
            <ul>
              <li>
                <a href="/">Accueil</a>
              </li>
              <li>
                <a href="/temp-maintien">Maintien en temp√©rature</a>
              </li>
            </ul>
          </div>
        )}
      </nav>
    </Sidebar>
  );
}
