import React, { useState } from "react";
import styled from "styled-components";

const Sidebar = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #003483;
  color: white;
  text-align: center;
  height: 100%;
  width: 200px;
  position: fixed;
  left: 0;

  @media (max-width: 768px) {
    display: none;
  }

  nav {
    padding: 10px 15px;
    h1 {
      margin: 0;
      a {
        text-decoration: none;
        color: white;
      }
    }
    ul {
      padding: 0;
      list-style: none;
      li {
        margin-block-start: 0em;
        margin-bottom: 10px;
        a {
          text-decoration: none;
          color: white;
        }
      }
    }
  }
`;

const MobileMenu = styled.div`
  display: none;

  @media (max-width: 768px) {
    display: flex;
    flex-direction: column;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.5);
    z-index: 1;

    button {
      position: absolute;
      top: 10px;
      left: 10px;
      z-index: 2;
      background-color: transparent;
      border: none;
      cursor: pointer;

      i {
        color: white;
        font-size: 20px;
      }
    }

    ul {
      padding: 10px 15px;
      list-style: none;
      li {
        margin-block-start: 0em;
        margin-bottom: 10px;
        a {
          text-decoration: none;
          color: white;
        }
      }
    }
  }
`;

export default function SideBar() {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <div>
      <Sidebar>
        <nav>
          <h1>
            <a href="/">Tecnoland</a>
          </h1>
          <ul>
            <li>
              <a href="/">Accueil 2</a>
            </li>
            <li>
              <a href="/temp-maintien">Maintien en température</a>
            </li>
          </ul>
        </nav>
      </Sidebar>
      <MobileMenu>
        <button onClick={toggleMobileMenu}>
          <i className="fa-solid fa-bars"></i>
        </button>
        <ul>
          <li>
            <a href="/">Accueil 2</a>
          </li>
          <li>
            <a href="/temp-maintien">Maintien en température</a>
          </li>
        </ul>
      </MobileMenu>
    </div>
  );
}
