import React from "react";
import styled from "styled-components";

export function SideBar() {
  const Sidebar = styled.div`
    display: flex;
    flex-direction: column;
    background-color: #003483;
    color: white;
    text-align: center;
    height: 100%;
    width: 200px;
    position: fixed;
    left: 0;  // Pour maintenir la barre à gauche
    @media (max-width: 768px) {
        width:100%;
        height:80px;
      }
  `;

  return (
    <Sidebar>
      <h1>IndustrieCalculator by Tecnoland</h1>
    </Sidebar>
  );
}
