import React from 'react';
import styled from 'styled-components';

const PageContainer = styled.div`
  margin-left:200px;
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  background-color: #f0f0f0;
  padding: 20px;
  height:100vh;
  flex-wrap:wrap;

  @media (max-width: 768px) {
    width: 100%;
    height: auto;
    margin-left:auto;
  }
`;

const LeftColumn = styled.div`
  flex: 1;
  padding: 20px;
  @media (max-width: 768px) {
    width: 100%;
  }
`;

const RightColumn = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  @media (max-width: 768px) {
    width: 100%;
  }
`;

const PromoBox = styled.div`
  background-color: #003483;
  color: white;
  border-radius: 10px;
  padding: 20px;
  margin: 20px;
  text-align: center;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
`;

export default function HomePage() {
  return (
    <PageContainer>
      <LeftColumn>
        <h1>Présentation des Simulateurs</h1>
        <p>
          Ici, vous pouvez ajouter une description de vos simulateurs et de ce que Tecnoland peut offrir. Expliquez en détail les avantages et les fonctionnalités de vos simulateurs.
        </p>
      </LeftColumn>
      <RightColumn>
        <PromoBox>
          <h2>Tecnoland</h2>
          <p>La solution par excellence pour vos besoins de simulation. Demandez un devis dès aujourd'hui !</p>
          <button>Demandez un devis</button>
        </PromoBox>
        <PromoBox>
          <h2>Simulateurs de pointe</h2>
          <p>Découvrez nos simulateurs de pointe pour répondre à tous vos besoins en matière de test et de simulation.</p>
          <button>Demandez un devis</button>
        </PromoBox>
      </RightColumn>
    </PageContainer>
  );
}
